package constructors.constructorchaining;

public class ConstructorChainingUsingThis {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           Moon moon =new Moon();
           System.out.println("color of moon:" +moon.color);
           System.out.println("shape of moon:" +moon.shape);
           System.out.println("size of moon:" +moon.size);
	}

}
